#Función que devuelva el número de empleados de la empresa (es una función un poco tonta).
def numero_empleados(salarios):
    return (len(salarios))

print(numero_empleados(salarios))


#Función que devuelva el salario máximo de la empresa.
def obtener_salario_max(salarios):
    
    salario_max=salarios[0]       
    for salario in salarios:
        if salario>salario_max:
            salario_max=salario
    return salario_max
    
print(obtener_salario_max(salarios))

#Función que devuelva el salario mínimo de la empresa.
def obtener_salario_min(salarios):
    
    salario_min=salarios[0]       
    for salario in salarios:
        if salario<salario_min:
            salario_min=salario
    return salario_min
    
print(obtener_salario_min(salarios))

#Función que devuelva el salario medio de la empresa.
def obtener_salario_medio(salarios):
    total=0
    for salario in salarios:
        total+=salario

    media=total/len(salarios)
    return round(media,2)

print(obtener_salario_medio(salarios))

#Función que devuelva el número de empleados cuyo salario es superior o igual al salario medio.
def obtener_salarios_mayores_media(salarios):
    salario_mayor_media=0
    for salario in salarios:
        if salario > obtener_salario_medio(salarios):
            salario_mayor_media+=1
    return salario_mayor_media
        
print(obtener_salarios_mayores_media(salarios))

#Función que devuelva el número de empleados cuyo salario es inferior al salario medio.
def obtener_salarios_menores_media(salarios):
    salario_menor_media=0
    for salario in salarios:
        if salario<obtener_salario_medio(salarios):
            salario_menor_media+=1
    return salario_menor_media

print(obtener_salarios_menores_media(salarios))

#Función que devuelva una lista con los salarios superiores o igual al salario medio (usar un acumulador y append).
def obtener_lista_salarios_mayores_media(salarios):
    salarios_superiores_media=[]
    for salario in salarios:
        if salario>obtener_salario_medio(salarios):
            salarios_superiores_media.append(salario)
    return salarios_superiores_media

print(obtener_lista_salarios_mayores_media(salarios))

